import styled from "styled-components";
import { AiOutlineLeft, AiOutlineRight } from "react-icons/ai";

export const BannerWrapper = styled.div`
  margin-top: 60px;
  border-top: thin red solid;
  position: absolute;
  width: 100%;
  height: 300px;
  border-bottom: thin red solid;
  display: flex;
  justify-content: center;
  align-items: center;
`;

export const BannerContainer = styled.div`
  width: 100%;
  height: 100%;

`;

export const BannerItem = styled.div`
  display: ${ (props) => {
    console.log(props.image, props.bannerId);
    return props.image === props.bannerId ? 'block' : 'none' 
  }};
  @media screen {
    transition: 0.3s all ease;
  }
`;

export const ButtonContainer = styled.div`
  position: absolute;
  margin-top: 0;
  margin-bottom: 0;
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

export const Button = styled.div`
  width: 50px;
  height: 50px;
`;

export const ButtonLeft = styled(AiOutlineLeft)`
  width: 100%;
  height: 100%;
`;

export const ButtonRight = styled(AiOutlineRight)`
  width: 100%;
  height: 100%;
`;

export const ImageSequence = styled.div`
  position: absolute;
  border-radius: 10px;
  background-color: gray;
  height: 1.2rem;
  width: 2.5rem;
  color: white;
  right: 40px;
  bottom: 20px;
  font-size: 0.7rem;
  display: flex;
  justify-content: center;
  align-items: center;
`;
