import React, { useEffect, useState } from "react";
import {
  HeaderWrapper,
  HeaderContainer,
  CompanyName,
  ItemContainer,
  Item,
  MenuContainer,
  Login,
  CartWrapper,
  Cart,
} from "./HeaderComponents";

const Header = () => {
  const [isHidden, setIsHidden] = useState(false);
  useEffect(() => {
    /*
    window.addEventListener('scroll', (e) => {

    });
    */
    window.addEventListener("click", (e) => {
      reverseHidden();
    });
  }, []);

  function reverseHidden() {
    console.log(isHidden);
    setIsHidden(!isHidden);
    console.log(isHidden);
  }

  return (
    <HeaderWrapper hide={isHidden}>
      <HeaderContainer>
        <CompanyName>WISELY</CompanyName>
        <ItemContainer>
          <Item to="#">제품보기</Item>
          <Item to="#">브랜드 스토리</Item>
          <Item to="#">블로그</Item>
          <Item to="#">고객센터</Item>
        </ItemContainer>
        <MenuContainer>
          <Login to="#">로그인</Login>
          <CartWrapper>
            <Cart />
          </CartWrapper>
        </MenuContainer>
      </HeaderContainer>
    </HeaderWrapper>
  );
};

export default Header;
