import React, { useEffect, useState } from "react";
import {
  HeaderWrapper,
  HeaderContainer,
  CompanyName,
  ItemContainer,
  Item,
  MenuContainer,
  Login,
  CartWrapper,
  Cart,
} from "./HeaderComponents";

const Header = () => {
  const [isHidden, setIsHidden] = useState(false);
  let _isHidden = isHidden;

  useEffect( () => {
    window.addEventListener('click', () => {
      setIsHidden(!_isHidden);
    })
  }, []);
  useEffect( () => { _isHidden = !_isHidden; console.log(_isHidden, isHidden); return; }, [isHidden]);

  return (
    <HeaderWrapper hide={isHidden}>
      <HeaderContainer>
        <CompanyName>WISELY</CompanyName>
        <ItemContainer>
          <Item to="#">제품보기</Item>
          <Item to="#">브랜드 스토리</Item>
          <Item to="#">블로그</Item>
          <Item to="#">고객센터</Item>
        </ItemContainer>
        <MenuContainer>
          <Login to="#">로그인</Login>
          <CartWrapper>
            <Cart />
          </CartWrapper>
        </MenuContainer>
      </HeaderContainer>
    </HeaderWrapper>
  );
};

export default Header;
